import {Button, Table} from 'react-bootstrap'

function Item(props) {
    return (
        <div> 
            <b> {props.title} </b>
            <Table striped bordered hover style={styles.itemTableStyle}>
                <tbody >
                    <tr>
                    <th width="50px"> Hanja</th>
                    <td> {props.hj} </td>
                    </tr>
                    <tr>
                    <th> gt-oKo </th>
                    <td> {props.gt_oko} </td>
                    </tr>
                    {
                        (props.gt_nko)
